const button=document.querySelector(".hotel-search-button"),popup=document.querySelector(".modal"),formSearch=document.querySelector(".form-search"),arrivalInput=document.querySelector(".arrival-input"),departureInput=document.querySelector(".departure-input");button.onclick=function(){popup.classList.toggle("modal-not-show"),popup.classList.remove("modal-error")},formSearch.addEventListener("submit",function(e){arrivalInput.value&&departureInput.value||(e.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popup.classList.contains("modal-show")&&(popup.classList.add("modal-not-show"),popup.classList.remove("modal-error")))});
